using UnityEngine;namespace StarrySky{	public class Rotation : MonoBehaviour	{		public float m_Speed = 1f;		public bool m_AxisX = false;		public bool m_AxisY = false;		public bool m_AxisZ = false;		void Update()		{			if (m_AxisX)				transform.Rotate(m_Speed * Time.deltaTime, 0, 0);			if (m_AxisY)				transform.Rotate(0, m_Speed * Time.deltaTime, 0);			if (m_AxisZ)				transform.Rotate(0, 0, m_Speed * Time.deltaTime);		}	}}